mapper.select:
select  t3.u_name,t1.e_id,t1.e_topic, t1.create_time, t1.create_user
from t_email t1
join t_notice t2
on t1.e_id = t2.e_id
join t_user t3
on t1.create_user=t3.u_id

    t2.recive_man = #{userId}

        and t2.is_readed=#{emailSerchVo.isReaded}


        and t3.u_name like concat('%',#{SerchVo.sendMan},'%')


        and substring(t2.create_time,1,8)=#{SerchVo.reviceTime}
