mapper.select:
select m_name
from t_menu
where m_id = #{treeId}

mapper.update:
update t_menu set  m_name=#{mName} ,m_url=#{mUrl} ,m_icon=#{mIcon} ,m_sort=#{mSort} ,
                  m_desc=#{mDesc} ,is_navigtion=#{isNavigtion},
                  update_user=#{updateUser},update_time=#{updateTime}
where m_id=#{mId}
